mapper.select:
select max(function_code) as function_code from function_manage where 1=1


  and PARENT_APPLICATION_ID=#{parentApplicationId,jdbcType=BIGINT}


  and PARENT_APPLICATION_ID is null

select f.* from function_manage f LEFT JOIN acct_function_sys afs on afs.function_id=f.function_id where f.PARENT_APPLICATION_ID is null and f.IS_DELETE = 0
AND f.STATUS = #{status}
AND afs.SYS_ID = #{sysId}

select
f.*
from function_manage f
LEFT JOIN acct_function_sys afs on afs.function_id=f.function_id
where
 1=1
and f.IS_DELETE = 0


    AND f.PARENT_APPLICATION_ID = #{parentApplicationId}


     AND f.STATUS = #{status}


  AND afs.SYS_ID = #{sysId}

SELECT CASE f2.functionstatus WHEN 0 THEN 0 ELSE 1 END AS status,f2.* FROM ( SELECT f1.*, IFNULL( ( SELECT IFNULL(f.FUNCTION_ID, 0) FROM function_manage f LEFT JOIN acct_oper_priv_rela ao ON ao.FUNCTION_ID = f.function_id LEFT JOIN role_info ri ON ri.ROLE_ID = ao.ROLE_ID WHERE f. STATUS = 1
AND ri.role_id = #{roleId}
AND f1.function_id = f.function_id ), 0 ) AS functionstatus FROM function_manage f1 WHERE f1. STATUS = 1 ) AS f2 LEFT JOIN acct_function_sys afs on afs.function_id=f2.function_id WHERE f2.PARENT_APPLICATION_ID IS NULL AND f2. STATUS = 1 AND f2.IS_DELETE = 0 and afs.sys_id = #{sysId}

SELECT CASE f2.functionstatus WHEN 0 THEN 0 ELSE 1 END AS status,f2.* FROM ( SELECT f1.*, IFNULL( ( SELECT IFNULL(f.FUNCTION_ID, 0) FROM function_manage f LEFT JOIN acct_oper_priv_rela ao ON ao.FUNCTION_ID = f.function_id LEFT JOIN role_info ri ON ri.ROLE_ID = ao.ROLE_ID WHERE f. STATUS = 1
AND ri.role_id = #{roleId}
AND f1.function_id = f.function_id ), 0 ) AS functionstatus FROM function_manage f1 WHERE f1. STATUS = 1 ) AS f2 LEFT JOIN acct_function_sys afs on afs.function_id=f2.function_id WHERE 1=1 AND f2. STATUS = 1 AND f2.IS_DELETE = 0
AND f2.PARENT_APPLICATION_ID = #{parentApplicationId}
and afs.sys_id = #{sysId}

select * from function_manage f
  LEFT JOIN acct_function_sys afs on afs.function_id=f.function_id
where 1=1 and f.IS_DELETE=0


  AND f.FUNCTION_TITLE = #{functionTitle}

and afs.sys_id = #{sysId}
