mapper.select:
select
from role_info where 1=1
AND role_id = #{roleId}

select r.role_group_id,r.role_group_title as role_group_title,'角色组' as orRole from role_group r where r.is_delete=0 and r.PARENT_APPLICATION_ID is null
AND r.ORG_ID in
#{all}

select r.role_group_id,r.role_group_title as role_group_title,r.PARENT_APPLICATION_ID as parentApplicationId,'角色组' as orRole from role_group r
where r.is_delete=0

  AND r.PARENT_APPLICATION_ID = #{parentApplicationId}


  AND r.ORG_ID in

    #{all}

select r.*,'角色' as orRole from role_info r LEFT JOIN acct_role_group_role ar on ar.role_id=r.role_id LEFT JOIN role_group rg on rg.role_group_id=ar.role_group_id where r.is_delete=0 and rg.role_group_id is null
AND r.ORG_ID in
#{all}

select r.*,'角色' as orRole,rg.role_group_id,rg.role_group_title from role_info r LEFT JOIN acct_role_group_role ar on ar.role_id=r.role_id LEFT JOIN role_group rg on rg.role_group_id=ar.role_group_id where r.is_delete=0 and rg.is_delete=0 AND ar.role_id IS not NULL
and rg.role_group_id = #{roleGroupId}
AND r.ORG_ID in
#{all}

SELECT
ri.role_id,rg.ROLE_GROUP_TITLE,concat(ri.role_title,case when rg.ROLE_GROUP_TITLE is null then '' else concat('(',rg.ROLE_GROUP_TITLE,')') end) as role_title
FROM
role_info ri
LEFT JOIN acct_role_group_role ar on ar.ROLE_ID=ri.ROLE_ID
LEFT JOIN role_group rg on rg.ROLE_GROUP_ID=ar.ROLE_GROUP_ID


    AND ri.IS_DELETE = #{isDelete}


    AND ri.STATUS = #{status}



    AND ri.ORG_ID =#{orgId,jdbcType=VARCHAR}

SELECT
ri.role_id,rg.ROLE_GROUP_TITLE,concat(ri.role_title,case when rg.ROLE_GROUP_TITLE is null then '' else concat('(',rg.ROLE_GROUP_TITLE,')') end) as role_title
FROM
role_info ri
LEFT JOIN acct_role_group_role ar on ar.ROLE_ID=ri.ROLE_ID
LEFT JOIN role_group rg on rg.ROLE_GROUP_ID=ar.ROLE_GROUP_ID


    AND ri.IS_DELETE = #{isDelete}


    AND ri.STATUS = #{status}



    AND ri.ORG_ID in

      #{all}

select ai.role_title,rg.ROLE_GROUP_ID from role_info ai
LEFT JOIN acct_role_group_role ar on ar.ROLE_ID=ai.ROLE_ID
LEFT JOIN role_group rg on rg.ROLE_GROUP_ID=ar.ROLE_GROUP_ID
where 1=1 and ai.IS_DELETE=0

  AND ai.ORG_ID = #{orgId}


  AND ai.ROLE_TITLE = #{roleTitle}


  and rg.role_group_id = #{roleGroupId}

select ai.* from role_info ai
where 1=1 and ai.IS_DELETE=0

  AND ai.ORG_ID = #{orgId}


  AND ai.ROLE_TITLE = #{roleTitle}

select IFNULL(c.orAcct, 0) as orAcct,c.role_id,c.role_title from (select ( SELECT CASE IFNULL(ai.ACCT_ID, 0) when 0 then 0 else 1 end FROM role_info ri LEFT JOIN acct_role_real ar ON ar.ROLE_ID = ri.ROLE_ID LEFT JOIN acct_info ai ON ai.ACCT_ID = ar.ACCT_ID WHERE ri.STATUS = 1 and ri.IS_DELETE = 0
AND ri.ORG_ID = #{orgId}
AND ai.acct_id = #{acctId}
and ri.role_id=c.role_id )as orAcct,c.role_id,rg.ROLE_GROUP_TITLE,concat(c.role_title,case when rg.ROLE_GROUP_TITLE is null then '' else concat('(',rg.ROLE_GROUP_TITLE,')') end) as role_title from role_info c LEFT JOIN acct_role_group_role ar on ar.ROLE_ID=c.ROLE_ID LEFT JOIN role_group rg on rg.ROLE_GROUP_ID=ar.ROLE_GROUP_ID WHERE 1=1
AND c.ORG_ID = #{orgId}
and c.IS_DELETE = 0 ) as c
