mapper.select:
select c.* from (select pe.*, GROUP_CONCAT(ri.ROLE_title ORDER BY rpr.ROLE_ID) as ROLE_name, rolepc.EXPRESSION as EXPRESSION,3 type from POWER_EXPRESSION pe LEFT JOIN ROLE_PRIV_RELATION rpr on rpr.POWER_EXPRESSION_id=pe.POWER_EXPRESSION_id LEFT JOIN PRIV_CONDITION_CONFIG rolepc on rolepc.id=rpr.RELATION_ID LEFT JOIN role_info ri on ri.role_id=rpr.role_id where 1=1 and pe.TABLE_ID=#{tableId} and rolepc.id is not null GROUP BY rolepc.id union ALL select pe.*, GROUP_CONCAT(oi.org_name ORDER BY opr.ORG_ID) as ROLE_name, orgpc.EXPRESSION as EXPRESSION,1 type from POWER_EXPRESSION pe LEFT JOIN ORG_PRIV_RELATION opr on opr.POWER_EXPRESSION_id=pe.POWER_EXPRESSION_id LEFT JOIN PRIV_CONDITION_CONFIG orgpc on orgpc.id=opr.RELATION_ID LEFT JOIN org_info oi on oi.id=opr.org_id where 1=1 and pe.TABLE_ID=#{tableId} and orgpc.id is not null GROUP BY orgpc.id union ALL select pe.*, GROUP_CONCAT(ai.acct_title ORDER BY apr.acct_id) as ROLE_name, accpc.EXPRESSION as EXPRESSION,2 type from POWER_EXPRESSION pe LEFT JOIN ACCOUNT_PRIV_RELATION apr on apr.POWER_EXPRESSION_id=pe.POWER_EXPRESSION_id LEFT JOIN PRIV_CONDITION_CONFIG accpc on accpc.id=apr.RELATION_ID LEFT JOIN acct_info ai on ai.acct_id=apr.acct_id where 1=1 and pe.TABLE_ID=#{tableId} and accpc.id is not null GROUP BY accpc.id ) as c where 1=1
and c.TABLE_ID = #{tableId,jdbcType=BIGINT}
and c.ORG_ID = #{orgId,jdbcType=VARCHAR}
and c.POWER_EXPRESSION_ID in (select d.POWER_EXPRESSION_ID from (select pe.POWER_EXPRESSION_ID from POWER_EXPRESSION pe where 1=1
and pe.TABLE_ID = #{tableId,jdbcType=BIGINT}
and pe.ORG_ID = #{orgId,jdbcType=VARCHAR} limit #{num},#{size}) as d ) ORDER BY c.POWER_EXPRESSION_id,c.role_name

select COUNT(pe.POWER_EXPRESSION_ID)
  from POWER_EXPRESSION pe
  where 1=1

      and pe.TABLE_ID = #{tableId,jdbcType=BIGINT}


      and pe.ORG_ID = #{orgId,jdbcType=VARCHAR}
